module Main where

import Data.ByteString (hPut)
import Data.ByteString.Base64 (decode)
import Data.ByteString.Char8 (ByteString, pack)
import qualified Notify as N
import qualified Quote as Q
import System.IO (hFlush)
import System.IO.Temp (withSystemTempFile)

main :: IO ()
main = withSystemTempFile "icon.png" (\path handle -> do
        hPut handle icon
        hFlush handle

        (quote, author) <- Q.getQuote
        N.show $ N.defautlConfig { N.title = "維基語錄", N.summary = author, N.content = quote, N.icon = path }
    )

icon :: ByteString
icon = icon
    where Right icon = decode . pack $ ""